package java07_4week;
// ********************* 구구단 짝수단  ************************* //

public class java0727_3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 2; i<=9; i+=2 ) {
			for( int j=1; j<=9; j++) {
				System.out.println( i + "*" + j + "=" + i*j);
			}
		}
	}

}
